import React, { useState } from 'react';
import { Text, View, TouchableOpacity } from 'react-native';
import { Video } from 'expo-av';   
import { globalStyles } from '../../styles/global';
import { BlurView } from 'expo-blur';

export default function HomeView() {
  var playbackObject; 

  const videos = ['https://stream.mux.com/sAGaxbuUsS8o5oQt6A9CAGnVsN026B011EjZ02GeZnVqLU.m3u8',
                    'https://stream.mux.com/8hfLK3MkXGDa380002B6ZyOVOVttBRAEL1l015eG7FjUrI.m3u8',
                    'https://stream.mux.com/mi00csRmf5dlTPZXB3102RNd5LWl4twN3sBfQMQpe4vUk.m3u8']

  const questions = ['Who is your best friend?', 'Where did you grow up?', 'What is your favorite color?']

  const [index, setIndex] = useState(0);

  const _handleVideoRef = component => {
    playbackObject = component;
  };

  const onPress = () => {
    setIndex(index + 1 === videos.length ? 0 : index + 1);    
    playbackObject.replayAsync()
  }

  return (
    <View style={{ flex: 1}}>
     <TouchableOpacity onPress={onPress} style={{flex: 50}}>  
        <Video
          source={{ uri: videos[index]}}
          ref={_handleVideoRef}
          rate={1.0}
          volume={1.0}
          isMuted={false}
          resizeMode="cover"
          usePoster={true}
          shouldPlay
          isLooping
          style={{ width: '100%', height: '90%'}}
        >
        </Video>

        <BlurView tint="dark" intensity={20} style={globalStyles.questionContainer}>
          <Text style={{fontSize: 20,color: "#eee", padding: 15}}>{questions[index]}</Text>
        </BlurView>
      </TouchableOpacity>
      <View style={{flex: 10, alignItems: 'center', justifyContent: 'center'}}>
        <Text>This is where the heart will go!</Text>
      </View>
    </View>
  );
}
